import styled, { css } from "styled-components";
import {verdeEscuro, verdeClaro, textoEscuro} from "../../UI/variaveis";


export const NavLink = styled.a `
    font-size: 18px;
    text-decoration: none;
    color: #fff;
`;

export const NavMobile = styled.nav `
    display: flex;
    position: absolute;
    top: 100px;
`;


export const Container = styled.section`
    position: absolute;
    backdrop-filter: blur(3px);
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: 9;
    display: flex;
    align-items: center;
    justify-content: center;

    background-color: ##5B945B;

    opacity: 0;
    pointer-events: none;
    flex-direction: column;
    
    
    
    > svg {
        position: absolute;
        top: 1rem;
        right: 1rem;
    }

    > img {
        position: absolute;
        top: 1.25rem;
        left: 1rem;
    }
    
    nav {
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
        gap: 2rem;
    }
    
    ${({ isVisible }) => isVisible && css`
        opacity: 1;
        pointer-events: auto;
        
    `};
`;
