import React, { Component } from 'react';
import styled from 'styled-components';
import { verdeEscuro } from '../UI/variaveis';

const Wrapper = styled.div`

display: flex;
flex-direction: row;
flex-wrap: wrap;
width: 480px;
align-items: center;
justify-content: center;
background: #ECECE2;
padding: 20px 10px;
border-radius: 8px;
border: solid 1px ${verdeEscuro};
box-shadow: 0px 5px 0px #115850;


`;

const Left = styled.div`
display: flex;
flex-direction: row;
flex-wrap: nowrap;
width: 80%;
align-items: center;
justify-content: center;
float: left;

 >h4 {
     width: 100%;
 }
 > span {
     width: 100%;
 }

`;

const Right = styled.div`

float: right;
border-radius: 100%;
background: ${verdeEscuro};
padding: 20px 30px;

> p {
    color: #fff;
    font-family: 'ITC Avant Garde Gothic Std';
}

`;


class Services extends Component {
    render() {
        return (
            <section>
                <Wrapper>
                    <Left>
                        <img src="#" /><div><h4>Entrega imediata</h4> <span>Melhor tempo de entrega</span></div>
                    </Left>
                    <Right>
                        <p>?</p>
                    </Right>
                </Wrapper>
            </section>
        );
    }
}


export default Services;
