import styled from "styled-components";
import {verdeEscuro, verdeClaro} from "../UI/variaveis";

const cabecalho = styled.div `
background: 
`;

export const HeaderWrapp = styled.div``;
export const TopBar = styled.div`
background: ${verdeEscuro};
display: flex;
flex-wrap: nowrap;
flex-direction: row;
align-content: center;
align-items: center;
justify-content: space-between;
`;
export const HomeHeader = styled.div`
  background: #5b945b;
  color: #fff;
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  padding: 24px 64px;
  align-content: center;
  align-items: center;
`;

export const Desconto = styled.div`
  width: 18%;
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  align-items: center;
  justify-content: space-between;
  color: #fff;
  font-weight: 600;
  padding-top: 12px;
  padding-left: 16px;
  padding-bottom: 9px;
`;

export const GiftCard = styled.div`
  width: 32%;
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  align-items: center;
  justify-content: flex-end;
  color: #fff;
  font-weight: 600;
  padding-top: 12px;
  padding-left: 16px;
  padding-bottom: 9px;
  align-content: center;
`;

export const FreteP = styled.p `
  color: #fff;
  margin-right: 16px;
  margin-left: 16px;
`;
export const Frete = styled.div `
  width: 32%;
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  align-content: center;
  justify-content: flex-end;
  align-items: center;
`;

export const Button = styled.button `
  margin-left: 24px;
  padding: 16px;
  background-color: ${(props) => props.primary ? verdeClaro : "transparent" };
  color: ${(props) => props.primary ? "#fff" : verdeEscuro};
  border: solid 1px #fff;
  border-radius: 13px;
`;
