import styled from "styled-components";

export const Container = styled.section`
    position: absolute;
    backdrop-filter: blur(3px);
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: 5;
    display: flex;
    align-items: center;
    justify-content: center;

    background-color: #fff;

    opacity: 0;


    > a {
        position: absolute;
        top: 1rem;
        right: 1rem;
    }

    nav {
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
        gap: 2rem;
    }
    ${({ isVisible }) => isVisible && css`
    
    opacity: 1;
    ` }
`;
